SELECT e.nombre AS nombre_empleado, e.celular, CASE WHEN m.respuesta IS NULL OR m.respuesta = 0 THEN 'No contestó' WHEN m.respuesta = 1 THEN 'Sí va' WHEN m.respuesta = 2 THEN 'No va ir' ELSE 'Desconocido' END AS respuesta, COALESCE(pe.lugar, 'Sin asignar') AS punto_encuentro FROM tb_employees e LEFT JOIN tb_mensajes m ON m.id_employee = e.id_empleado LEFT JOIN tb_punto_encuentro pe ON pe.id_empleado = e.id_empleado ORDER BY e.nombre ASC;